import { combineReducers, createStore } from "redux";
import authReduser from "./auth/AuthReduser";
import { calculatorReducer } from "./calculator/calculatorReduser";
import { calculatorActionTypes } from "../store/calculator/calculatorReduser";

const rootReduser = combineReducers({
  calculator: calculatorReducer,
  auth: authReduser,
});

export const store = createStore(calculatorReducer);

store.subscribe(() => {
  console.log("STORE CHANGED", store.getState);
});
