import classes from './Counter.module.css'; 

import {calculatorActionTypes, store} from '../store'
const Calculator = () => {

const {result,} = store.getState()

const addHandler = () => {
  store.dispatch({type: calculatorActionTypes.ADD, payload: 5})

}

const subtractHandler = () => {
  store.dispatch({ type: calculatorActionTypes.SUBTRACT, payload: 4 });
};

const divideHandler = () => {
  store.dispatch({ type: calculatorActionTypes.DIVIDE, payload: 4 });
};

const multiplyHandler = () => {
  store.dispatch({ type: calculatorActionTypes.MULTIPLY, payload: 4 });
};

  return (
    <main className={classes.counter}>
      <h1>Redux Calculator</h1>
      <div className={classes.value}>Result: {result}</div>
      <button onClick={addHandler}>+5 </button>
      <button onClick={subtractHandler}>/4 </button>
      <button onClick={divideHandler}>/2 </button>
      <button onClick={multiplyHandler}>*4</button>
    </main>
  );
};

export default Calculator;
